import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { LoadingSpinner } from './auth';
import { useAuth, useAuthGuard } from '@/hooks/useAuth';
import { ROUTES } from '@/constants';

export interface ProtectedRouteProps {
  children: React.ReactNode;
  redirectTo?: string;
  requiredRole?: 'client' | 'freelancer' | 'admin';
  requireVerification?: boolean;
  requireActive?: boolean;
  fallback?: React.ReactNode;
}

const ProtectedRoute: React.FC<ProtectedRouteProps> = ({
  children,
  redirectTo = ROUTES.LOGIN,
  requiredRole,
  requireVerification = false,
  requireActive = false,
  fallback,
}) => {
  const location = useLocation();
  const { isAuthenticated, isLoading, user } = useAuth();
  const { canAccess, requireRole, requireVerification: checkVerification, requireActive: checkActive } = useAuthGuard();

  // Show loading spinner while checking authentication
  if (isLoading) {
    return fallback || (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <LoadingSpinner size="lg" text="éªŒè¯èº«ä»½ä¸­..." />
        </div>
      </div>
    );
  }

  // Redirect to login if not authenticated
  if (!isAuthenticated) {
    return (
      <Navigate
        to={redirectTo}
        state={{ from: location }}
        replace
      />
    );
  }

  // Check role requirements
  if (requiredRole && !requireRole(requiredRole)) {
    return (
      <Navigate
        to={ROUTES.DASHBOARD}
        replace
      />
    );
  }

  // Check verification requirements
  if (requireVerification && !checkVerification()) {
    return (
      <Navigate
        to={ROUTES.VERIFY_EMAIL}
        replace
      />
    );
  }

  // Check active status requirements
  if (requireActive && !checkActive()) {
    return (
      <Navigate
        to={ROUTES.DASHBOARD}
        replace
      />
    );
  }

  // User is authenticated and meets all requirements
  return <>{children}</>;
};

export default ProtectedRoute;

import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { cn } from '@/utils';

export interface BarChartProps {
  data: any[];
  bars: {
    dataKey: string;
    fill: string;
    name: string;
  }[];
  xAxisDataKey: string;
  title?: string;
  height?: number;
  className?: string;
  showGrid?: boolean;
  showLegend?: boolean;
  showTooltip?: boolean;
  orientation?: 'vertical' | 'horizontal';
}

const BarChartComponent: React.FC<BarChartProps> = ({
  data,
  bars,
  xAxisDataKey,
  title,
  height = 300,
  className = '',
  showGrid = true,
  showLegend = true,
  showTooltip = true,
  orientation = 'vertical',
}) => {
  return (
    <div className={cn('w-full', className)}>
      {title && (
        <h3 className="text-lg font-semibold text-gray-900 mb-4 leading-chinese">
          {title}
        </h3>
      )}
      <ResponsiveContainer width="100%" height={height}>
        <BarChart
          data={data}
          margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
          layout={orientation === 'horizontal' ? 'horizontal' : 'vertical'}
        >
          {showGrid && <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />}
          <XAxis
            dataKey={orientation === 'horizontal' ? undefined : xAxisDataKey}
            type={orientation === 'horizontal' ? 'number' : 'category'}
            stroke="#6b7280"
            tick={{ fill: '#6b7280', fontSize: 12 }}
            tickLine={{ stroke: '#e5e7eb' }}
          />
          <YAxis
            dataKey={orientation === 'horizontal' ? xAxisDataKey : undefined}
            type={orientation === 'horizontal' ? 'category' : 'number'}
            stroke="#6b7280"
            tick={{ fill: '#6b7280', fontSize: 12 }}
            tickLine={{ stroke: '#e5e7eb' }}
          />
          {showTooltip && (
            <Tooltip
              contentStyle={{
                backgroundColor: '#ffffff',
                border: '1px solid #e5e7eb',
                borderRadius: '8px',
                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
              }}
              labelStyle={{ color: '#111827', fontWeight: 'bold' }}
            />
          )}
          {showLegend && <Legend />}
          {bars.map((bar, index) => (
            <Bar
              key={index}
              dataKey={bar.dataKey}
              fill={bar.fill}
              name={bar.name}
              radius={[8, 8, 0, 0]}
            />
          ))}
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default BarChartComponent;

import React, { useState } from 'react';
import { Calendar, ChevronDown } from 'lucide-react';
import { cn } from '@/utils';

export interface DateRangeOption {
  label: string;
  value: string;
  days?: number;
  startDate?: Date;
  endDate?: Date;
}

export interface DateRangeSelectorProps {
  options: DateRangeOption[];
  selectedValue?: string;
  onChange?: (option: DateRangeOption) => void;
  className?: string;
  placeholder?: string;
  disabled?: boolean;
}

const DEFAULT_OPTIONS: DateRangeOption[] = [
  { label: 'æœ€è¿‘7å¤©', value: '7d', days: 7 },
  { label: 'æœ€è¿‘30å¤©', value: '30d', days: 30 },
  { label: 'æœ€è¿‘90å¤©', value: '90d', days: 90 },
  { label: 'æœ€è¿‘6ä¸ªæœˆ', value: '6m', days: 180 },
  { label: 'æœ€è¿‘1å¹´', value: '1y', days: 365 },
  { label: 'è‡ªå®šä¹‰', value: 'custom' },
];

const DateRangeSelector: React.FC<DateRangeSelectorProps> = ({
  options = DEFAULT_OPTIONS,
  selectedValue = '30d',
  onChange,
  className = '',
  placeholder = 'é€‰æ‹©æ—¶é—´èŒƒå›´',
  disabled = false,
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState<DateRangeOption>(
    options.find(opt => opt.value === selectedValue) || options[0]
  );

  const handleSelect = (option: DateRangeOption) => {
    setSelectedOption(option);
    setIsOpen(false);
    onChange?.(option);
  };

  return (
    <div className={cn('relative', className)}>
      <button
        type="button"
        onClick={() => !disabled && setIsOpen(!isOpen)}
        disabled={disabled}
        className={cn(
          'inline-flex items-center gap-2 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm',
          'text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-red-500',
          'disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200',
          disabled && 'bg-gray-100 cursor-not-allowed'
        )}
      >
        <Calendar className="w-4 h-4 text-gray-400" />
        <span>{selectedOption.label}</span>
        <ChevronDown className={cn(
          'w-4 h-4 text-gray-400 transition-transform duration-200',
          isOpen && 'transform rotate-180'
        )} />
      </button>

      {isOpen && (
        <>
          <div
            className="fixed inset-0 z-10"
            onClick={() => setIsOpen(false)}
          />
          <div className="absolute right-0 z-20 mt-2 w-56 bg-white rounded-lg shadow-lg border border-gray-200 py-1">
            {options.map((option) => (
              <button
                key={option.value}
                type="button"
                onClick={() => handleSelect(option)}
                className={cn(
                  'w-full px-4 py-2 text-left text-sm hover:bg-gray-50 transition-colors duration-150',
                  'focus:outline-none focus:bg-gray-50',
                  selectedOption.value === option.value
                    ? 'bg-red-50 text-red-700 font-medium'
                    : 'text-gray-700'
                )}
              >
                {option.label}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default DateRangeSelector;

import React, { useState } from 'react';
import { Download, FileText, FileSpreadsheet, FileImage } from 'lucide-react';
import { cn } from '@/utils';

export interface ExportDataProps {
  data: any[];
  filename?: string;
  formats?: ('csv' | 'excel' | 'pdf' | 'json')[];
  onExport?: (format: string, data: any[]) => void;
  className?: string;
  disabled?: boolean;
}

const ExportData: React.FC<ExportDataProps> = ({
  data,
  filename = 'export_data',
  formats = ['csv', 'excel', 'json'],
  onExport,
  className = '',
  disabled = false,
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isExporting, setIsExporting] = useState<string | null>(null);

  const formatOptions = [
    { value: 'csv', label: 'CSV', icon: <FileText className="w-4 h-4" /> },
    { value: 'excel', label: 'Excel', icon: <FileSpreadsheet className="w-4 h-4" /> },
    { value: 'json', label: 'JSON', icon: <FileText className="w-4 h-4" /> },
    { value: 'pdf', label: 'PDF', icon: <FileImage className="w-4 h-4" /> },
  ].filter(option => formats.includes(option.value as any));

  const downloadCSV = (data: any[]) => {
    if (!data.length) return;

    const headers = Object.keys(data[0]);
    const csvContent = [
      headers.join(','),
      ...data.map(row =>
        headers.map(header => {
          const value = row[header];
          return typeof value === 'string' && value.includes(',')
            ? `"${value.replace(/"/g, '""')}"`
            : value;
        }).join(',')
      )
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `${filename}.csv`;
    link.click();
    URL.revokeObjectURL(link.href);
  };

  const downloadJSON = (data: any[]) => {
    const blob = new Blob([JSON.stringify(data, null, 2)], {
      type: 'application/json;charset=utf-8;'
    });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `${filename}.json`;
    link.click();
    URL.revokeObjectURL(link.href);
  };

  const handleExport = async (format: string) => {
    if (disabled || isExporting) return;

    setIsExporting(format);

    try {
      if (onExport) {
        await onExport(format, data);
      } else {
        switch (format) {
          case 'csv':
            downloadCSV(data);
            break;
          case 'json':
            downloadJSON(data);
            break;
          case 'excel':
            // Excel export would require a library like xlsx
            console.log('Excel export not implemented');
            break;
          case 'pdf':
            // PDF export would require a library like jspdf
            console.log('PDF export not implemented');
            break;
        }
      }
    } catch (error) {
      console.error('Export failed:', error);
    } finally {
      setIsExporting(null);
      setIsOpen(false);
    }
  };

  return (
    <div className={cn('relative', className)}>
      <button
        type="button"
        onClick={() => !disabled && setIsOpen(!isOpen)}
        disabled={disabled}
        className={cn(
          'inline-flex items-center gap-2 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm',
          'text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-red-500',
          'disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200'
        )}
      >
        <Download className="w-4 h-4 text-gray-400" />
        <span>å¯¼å‡ºæ•°æ®</span>
      </button>

      {isOpen && (
        <>
          <div
            className="fixed inset-0 z-10"
            onClick={() => setIsOpen(false)}
          />
          <div className="absolute right-0 z-20 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-1">
            {formatOptions.map((option) => (
              <button
                key={option.value}
                type="button"
                onClick={() => handleExport(option.value)}
                disabled={isExporting === option.value}
                className={cn(
                  'w-full px-4 py-2 text-left text-sm hover:bg-gray-50 transition-colors duration-150',
                  'focus:outline-none focus:bg-gray-50 flex items-center gap-2',
                  'text-gray-700 disabled:opacity-50 disabled:cursor-not-allowed'
                )}
              >
                {option.icon}
                <span>{option.label}</span>
                {isExporting === option.value && (
                  <div className="ml-auto">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-red-500"></div>
                  </div>
                )}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default ExportData;

import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { cn } from '@/utils';

export interface LineChartProps {
  data: any[];
  lines: {
    dataKey: string;
    stroke: string;
    name: string;
    strokeWidth?: number;
  }[];
  xAxisDataKey: string;
  title?: string;
  height?: number;
  className?: string;
  showGrid?: boolean;
  showLegend?: boolean;
  showTooltip?: boolean;
}

const LineChartComponent: React.FC<LineChartProps> = ({
  data,
  lines,
  xAxisDataKey,
  title,
  height = 300,
  className = '',
  showGrid = true,
  showLegend = true,
  showTooltip = true,
}) => {
  return (
    <div className={cn('w-full', className)}>
      {title && (
        <h3 className="text-lg font-semibold text-gray-900 mb-4 leading-chinese">
          {title}
        </h3>
      )}
      <ResponsiveContainer width="100%" height={height}>
        <LineChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          {showGrid && <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />}
          <XAxis
            dataKey={xAxisDataKey}
            stroke="#6b7280"
            tick={{ fill: '#6b7280', fontSize: 12 }}
            tickLine={{ stroke: '#e5e7eb' }}
          />
          <YAxis
            stroke="#6b7280"
            tick={{ fill: '#6b7280', fontSize: 12 }}
            tickLine={{ stroke: '#e5e7eb' }}
          />
          {showTooltip && (
            <Tooltip
              contentStyle={{
                backgroundColor: '#ffffff',
                border: '1px solid #e5e7eb',
                borderRadius: '8px',
                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
              }}
              labelStyle={{ color: '#111827', fontWeight: 'bold' }}
            />
          )}
          {showLegend && <Legend />}
          {lines.map((line, index) => (
            <Line
              key={index}
              type="monotone"
              dataKey={line.dataKey}
              stroke={line.stroke}
              name={line.name}
              strokeWidth={line.strokeWidth || 2}
              dot={{ fill: line.stroke, strokeWidth: 2, r: 4 }}
              activeDot={{ r: 6 }}
            />
          ))}
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default LineChartComponent;

import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import { cn } from '@/utils';

export interface PieChartProps {
  data: {
    name: string;
    value: number;
    color?: string;
  }[];
  title?: string;
  height?: number;
  className?: string;
  showLegend?: boolean;
  showTooltip?: boolean;
  innerRadius?: number;
  outerRadius?: number;
  showLabels?: boolean;
}

const DEFAULT_COLORS = [
  '#ef4444', // red-500
  '#f59e0b', // amber-500
  '#10b981', // emerald-500
  '#3b82f6', // blue-500
  '#8b5cf6', // violet-500
  '#ec4899', // pink-500
  '#06b6d4', // cyan-500
  '#84cc16', // lime-500
];

const PieChartComponent: React.FC<PieChartProps> = ({
  data,
  title,
  height = 300,
  className = '',
  showLegend = true,
  showTooltip = true,
  innerRadius = 0,
  outerRadius = 80,
  showLabels = true,
}) => {
  const chartData = data.map((item, index) => ({
    ...item,
    color: item.color || DEFAULT_COLORS[index % DEFAULT_COLORS.length],
  }));

  const renderLabel = (entry: any) => {
    if (!showLabels) return null;
    const percentage = ((entry.value / data.reduce((sum, item) => sum + item.value, 0)) * 100).toFixed(1);
    return `${percentage}%`;
  };

  return (
    <div className={cn('w-full', className)}>
      {title && (
        <h3 className="text-lg font-semibold text-gray-900 mb-4 leading-chinese">
          {title}
        </h3>
      )}
      <ResponsiveContainer width="100%" height={height}>
        <PieChart>
          <Pie
            data={chartData}
            cx="50%"
            cy="50%"
            labelLine={false}
            label={renderLabel}
            outerRadius={outerRadius}
            innerRadius={innerRadius}
            fill="#8884d8"
            dataKey="value"
          >
            {chartData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Pie>
          {showTooltip && (
            <Tooltip
              contentStyle={{
                backgroundColor: '#ffffff',
                border: '1px solid #e5e7eb',
                borderRadius: '8px',
                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
              }}
              formatter={(value: number) => [`${value}`, 'æ•°é‡']}
            />
          )}
          {showLegend && (
            <Legend
              verticalAlign="middle"
              align="right"
              layout="vertical"
              formatter={(value) => <span className="text-sm text-gray-700">{value}</span>}
            />
          )}
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PieChartComponent;

import React, { useState, useEffect } from 'react';
import { cn } from '@/utils';
import Button from '@/components/ui/Button';
import FormInput from './FormInput';
import FormError from './FormError';
import LoadingSpinner from './LoadingSpinner';

export interface EmailVerificationProps {
  email: string;
  onVerificationSuccess?: () => void;
  onResend?: (email: string) => Promise<void>;
  className?: string;
}

const EmailVerification: React.FC<EmailVerificationProps> = ({
  email,
  onVerificationSuccess,
  onResend,
  className,
}) => {
  const [code, setCode] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isResending, setIsResending] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [countdown, setCountdown] = useState(0);
  const [isVerified, setIsVerified] = useState(false);

  // Countdown timer for resend button
  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    }
  }, [countdown]);

  // Handle verification code input (auto-focus next input)
  const handleCodeChange = (value: string, index: number) => {
    // Only allow numbers
    const numericValue = value.replace(/\D/g, '');

    if (numericValue.length <= 1) {
      const newCode = code.split('');
      newCode[index] = numericValue;
      setCode(newCode.join(''));

      // Auto-focus next input
      if (numericValue && index < 5) {
        const nextInput = document.getElementById(`code-input-${index + 1}`) as HTMLInputElement;
        if (nextInput) {
          nextInput.focus();
        }
      }
    }
  };

  // Handle key press for backspace navigation
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>, index: number) => {
    if (e.key === 'Backspace' && !code[index] && index > 0) {
      const prevInput = document.getElementById(`code-input-${index - 1}`) as HTMLInputElement;
      if (prevInput) {
        prevInput.focus();
      }
    }
  };

  // Handle paste event
  const handlePaste = (e: React.ClipboardEvent) => {
    e.preventDefault();
    const pastedData = e.clipboardData.getData('text').replace(/\D/g, '').slice(0, 6);
    if (pastedData.length === 6) {
      setCode(pastedData);
      // Focus last input
      const lastInput = document.getElementById('code-input-5') as HTMLInputElement;
      if (lastInput) {
        lastInput.focus();
      }
    }
  };

  // Verify email code
  const handleVerify = async () => {
    if (code.length !== 6) {
      setError('è¯·è¾“å…¥å®Œæ•´çš„6ä½éªŒè¯ç ');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // Mock API call - replace with actual verification
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Simulate success/failure
      if (code === '123456') {
        setIsVerified(true);
        onVerificationSuccess?.();
      } else {
        setError('éªŒè¯ç é”™è¯¯ï¼Œè¯·é‡æ–°è¾“å…¥');
      }
    } catch (error) {
      setError(error instanceof Error ? error.message : 'éªŒè¯å¤±è´¥ï¼Œè¯·é‡è¯•');
    } finally {
      setIsLoading(false);
    }
  };

  // Resend verification code
  const handleResend = async () => {
    if (countdown > 0) return;

    setIsResending(true);
    setError(null);

    try {
      await onResend?.(email);

      // Start countdown
      setCountdown(60);
      setCode(''); // Clear existing code

      // Focus first input
      const firstInput = document.getElementById('code-input-0') as HTMLInputElement;
      if (firstInput) {
        firstInput.focus();
      }
    } catch (error) {
      setError(error instanceof Error ? error.message : 'å‘é€å¤±è´¥ï¼Œè¯·é‡è¯•');
    } finally {
      setIsResending(false);
    }
  };

  // Success state
  if (isVerified) {
    return (
      <div className={cn('text-center py-8', className)}>
        <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
          <svg
            className="w-8 h-8 text-green-500"
            fill="none"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path d="M5 13l4 4L19 7" />
          </svg>
        </div>
        <h3 className="text-lg font-semibold text-gray-900 mb-2">é‚®ç®±éªŒè¯æˆåŠŸ</h3>
        <p className="text-sm text-gray-600">æ‚¨çš„é‚®ç®± {email} å·²æˆåŠŸéªŒè¯</p>
      </div>
    );
  }

  return (
    <div className={cn('space-y-6', className)}>
      {/* Header */}
      <div className="text-center">
        <h3 className="text-lg font-semibold text-gray-900 mb-2">éªŒè¯æ‚¨çš„é‚®ç®±</h3>
        <p className="text-sm text-gray-600">
          æˆ‘ä»¬å·²å‘ <span className="font-medium text-gray-900">{email}</span> å‘é€äº†6ä½éªŒè¯ç 
        </p>
      </div>

      {/* Code Input Fields */}
      <div className="flex justify-center space-x-2">
        {[0, 1, 2, 3, 4, 5].map((index) => (
          <input
            key={index}
            id={`code-input-${index}`}
            type="text"
            inputMode="numeric"
            maxLength={1}
            value={code[index] || ''}
            onChange={(e) => handleCodeChange(e.target.value, index)}
            onKeyDown={(e) => handleKeyDown(e, index)}
            onPaste={index === 0 ? handlePaste : undefined}
