  showPreview?: boolean;
  autoGenerateThumbnail?: boolean;
}

const VideoUpload: React.FC<VideoUploadProps> = ({
  videos = [],
  onChange,
  maxVideos = 3,
  maxFileSize = 100 * 1024 * 1024, // 100MB
  acceptedFormats = ['mp4', 'avi', 'mov', 'wmv', 'flv', 'webm'],
  className = '',
  disabled = false,
  showPreview = true,
  autoGenerateThumbnail = true
}) => {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [dragActive, setDragActive] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [errors, setErrors] = useState<string[]>([]);

  const validateFile = (file: File): string[] => {
    const newErrors: string[] = [];

    // Check file type
    const extension = file.name.split('.').pop()?.toLowerCase();
    if (!extension || !acceptedFormats.includes(extension)) {
      newErrors.push(`ä¸æ”¯æŒçš„è§†é¢‘æ ¼å¼ã€‚è¯·ä½¿ç”¨: ${acceptedFormats.join(', ')}`);
    }

    // Check file size
    if (file.size > maxFileSize) {
      newErrors.push(`æ–‡ä»¶å¤§å°ä¸èƒ½è¶…è¿‡ ${(maxFileSize / 1024 / 1024).toFixed(1)}MB`);
    }

    return newErrors;
  };

  const generateThumbnail = (videoFile: File): Promise<string> => {
    return new Promise((resolve) => {
      const video = document.createElement('video');
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d')!;

      video.addEventListener('loadeddata', () => {
        // Set canvas dimensions to match video
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        // Seek to 1 second or 10% of video duration
        const seekTime = Math.min(1, video.duration * 0.1);
        video.currentTime = seekTime;
