    const newSettings = {
      ...settings,
      [category]: {
        ...settings[category as keyof ProfileSettings],
        [key]: value
      }
    };
    onChange(newSettings);
    setHasChanges(true);
  }, [settings, onChange]);

  const handleBasicSettingChange = useCallback((key: string, value: any) => {
    const newSettings = { ...settings, [key]: value };
    onChange(newSettings);
    setHasChanges(true);
