  { value: 'high_school', label: 'é«˜ä¸­', weight: 1 },
  { value: 'associate', label: 'ä¸“ç§‘', weight: 2 },
  { value: 'bachelor', label: 'æœ¬ç§‘', weight: 3 },
  { value: 'master', label: 'ç¡•å£«', weight: 4 },
