const COMMON_INSTITUTIONS = [
  'æ¸…åå¤§å­¦', 'åŒ—äº¬å¤§å­¦', 'å¤æ—¦å¤§å­¦', 'ä¸Šæµ·äº¤é€šå¤§å­¦', 'æµ™æ±Ÿå¤§å­¦',
  'å—äº¬å¤§å­¦', 'ä¸­å±±å¤§å­¦', 'åä¸­ç§‘æŠ€å¤§å­¦', 'è¥¿å®‰äº¤é€šå¤§å­¦', 'å“ˆå°”æ»¨å·¥ä¸šå¤§å­¦',
  'MIT', 'Stanford University', 'Harvard University', 'UC Berkeley', 'Oxford University',
