export const EducationManager: React.FC<EducationManagerProps> = ({
  education,
  onChange,
  maxItems = 10,
  enableReordering = true,
  showDocumentUpload = true
}) => {
  const { t } = useTranslation();
  const [isAddingEducation, setIsAddingEducation] = useState(false);
  const [editingEducation, setEditingEducation] = useState<EducationItem | null>(null);
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [newEducation, setNewEducation] = useState<Partial<EducationItem>>({
    institution: '',
    degree: '',
    field: '',
    startDate: '',
    endDate: '',
    isCurrent: false,
    diplomaDocument: ''
  });

  const handleAddEducation = useCallback(() => {
    if (!newEducation.institution?.trim() || !newEducation.degree?.trim() || !newEducation.field?.trim()) {
      toast.error(t('profile.education.requiredFields'));
      return;
    }

    if (education.length >= maxItems) {
      toast.error(t('profile.education.maxReached', { max: maxItems }));
      return;
    }

    const educationItem: EducationItem = {
      id: Date.now().toString(),
      institution: newEducation.institution!,
      degree: newEducation.degree!,
      field: newEducation.field!,
      startDate: newEducation.startDate!,
      endDate: newEducation.isCurrent ? '' : newEducation.endDate || '',
      isCurrent: newEducation.isCurrent || false,
      diplomaDocument: newEducation.diplomaDocument || ''
    };

    onChange([...education, educationItem]);
    setNewEducation({
      institution: '',
      degree: '',
      field: '',
      startDate: '',
      endDate: '',
      isCurrent: false,
      diplomaDocument: ''
    });
    setShowAddDialog(false);
    toast.success(t('profile.education.added'));
  }, [newEducation, education, onChange, maxItems, t]);

  const handleUpdateEducation = useCallback(() => {
    if (!editingEducation || !editingEducation.institution?.trim() || !editingEducation.degree?.trim() || !editingEducation.field?.trim()) {
      toast.error(t('profile.education.requiredFields'));
      return;
    }

    const updatedEducation = education.map(edu =>
      edu.id === editingEducation.id ? editingEducation : edu
    );
    onChange(updatedEducation);
    setEditingEducation(null);
    toast.success(t('profile.education.updated'));
  }, [editingEducation, education, onChange, t]);

  const handleDeleteEducation = useCallback((educationId: string) => {
    const updatedEducation = education.filter(edu => edu.id !== educationId);
    onChange(updatedEducation);
    toast.success(t('profile.education.deleted'));
  }, [education, onChange, t]);

  const handleReorderEducation = useCallback((index: number, direction: 'up' | 'down') => {
    if (!enableReordering) return;

    const reorderedEducation = [...education];
    const targetIndex = direction === 'up' ? index - 1 : index + 1;

    if (targetIndex >= 0 && targetIndex < reorderedEducation.length) {
      [reorderedEducation[index], reorderedEducation[targetIndex]] =
      [reorderedEducation[targetIndex], reorderedEducation[index]];
      onChange(reorderedEducation);
    }
  }, [education, onChange, enableReordering]);

  const toggleExpanded = useCallback((id: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(id)) {
      newExpanded.delete(id);
    } else {
      newExpanded.add(id);
    }
    setExpandedItems(newExpanded);
  }, [expandedItems]);

  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('zh-CN', { year: 'numeric', month: 'long' });
  };

  const calculateDuration = (startDate: string, endDate: string, isCurrent: boolean) => {
    const start = new Date(startDate);
    const end = isCurrent ? new Date() : new Date(endDate);

    const months = (end.getFullYear() - start.getFullYear()) * 12 + (end.getMonth() - start.getMonth());
    const years = Math.floor(months / 12);
    const remainingMonths = months % 12;

    if (years > 0) {
      return remainingMonths > 0
        ? `${years}å¹´${remainingMonths}ä¸ªæœˆ`
        : `${years}å¹´`;
    }
    return `${remainingMonths}ä¸ªæœˆ`;
  };

  const getEducationLevelWeight = (degree: string) => {
    const level = EDUCATION_LEVELS.find(l => l.label === degree || l.value === degree);
    return level ? level.weight : 0;
  };

  const sortEducationByLevel = (items: EducationItem[]) => {
    return [...items].sort((a, b) => {
      const weightA = getEducationLevelWeight(a.degree);
      const weightB = getEducationLevelWeight(b.degree);
      if (weightA !== weightB) {
        return weightB - weightA;
      }
      // If same level, sort by date (most recent first)
      const dateA = a.isCurrent ? new Date() : new Date(a.endDate || a.startDate);
      const dateB = b.isCurrent ? new Date() : new Date(b.endDate || b.startDate);
      return dateB.getTime() - dateA.getTime();
    });
  };

  const sortedEducation = sortEducationByLevel(education);

  const handleDocumentUpload = useCallback(async (file: File) => {
    // TODO: Implement actual file upload
    const mockDocumentUrl = URL.createObjectURL(file);
    return mockDocumentUrl;
  }, []);

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <GraduationCap className="h-5 w-5" />
              {t('profile.education.title')}
              <Badge variant="outline">{education.length}</Badge>
            </CardTitle>

            <Dialog open={showAddDialog} onOpenChange={setShowAddDialog}>
              <DialogTrigger asChild>
                <Button
                  disabled={education.length >= maxItems}
                  className="flex items-center gap-2"
                >
                  <Plus className="h-4 w-4" />
                  {t('profile.education.add')}
                </Button>
              </DialogTrigger>

              <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>{t('profile.education.addNew')}</DialogTitle>
                </DialogHeader>

                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="institution">{t('profile.education.institution')} *</Label>
                    <Input
                      id="institution"
                      value={newEducation.institution || ''}
                      onChange={(e) => setNewEducation({ ...newEducation, institution: e.target.value })}
                      placeholder={t('profile.education.institution.placeholder')}
                    />
                    {/* Suggestions */}
                    <div className="flex flex-wrap gap-2">
                      {COMMON_INSTITUTIONS.filter(inst =>
                        inst.toLowerCase().includes((newEducation.institution || '').toLowerCase())
                      ).slice(0, 5).map(inst => (
                        <Button
                          key={inst}
                          variant="outline"
                          size="sm"
                          onClick={() => setNewEducation({ ...newEducation, institution: inst })}
                          className="text-xs"
                        >
                          {inst}
                        </Button>
                      ))}
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="degree">{t('profile.education.degree')} *</Label>
                      <Select
                        value={newEducation.degree || ''}
                        onValueChange={(value) => setNewEducation({ ...newEducation, degree: value })}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder={t('profile.education.degree.placeholder')} />
                        </SelectTrigger>
                        <SelectContent>
                          {EDUCATION_LEVELS.map(level => (
                            <SelectItem key={level.value} value={level.label}>
                              {level.label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="field">{t('profile.education.field')} *</Label>
                      <Input
                        id="field"
                        value={newEducation.field || ''}
                        onChange={(e) => setNewEducation({ ...newEducation, field: e.target.value })}
                        placeholder={t('profile.education.field.placeholder')}
                      />
                      {/* Field suggestions */}
                      <div className="flex flex-wrap gap-2">
                        {FIELDS_OF_STUDY.filter(field =>
                          field.toLowerCase().includes((newEducation.field || '').toLowerCase())
                        ).slice(0, 3).map(field => (
                          <Button
                            key={field}
                            variant="outline"
                            size="sm"
                            onClick={() => setNewEducation({ ...newEducation, field })}
                            className="text-xs"
                          >
                            {field}
                          </Button>
                        ))}
                      </div>
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="startDate">{t('profile.education.startDate')} *</Label>
                      <Input
                        id="startDate"
                        type="month"
                        value={newEducation.startDate || ''}
                        onChange={(e) => setNewEducation({ ...newEducation, startDate: e.target.value })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="endDate">{t('profile.education.endDate')}</Label>
                      <Input
                        id="endDate"
                        type="month"
                        value={newEducation.endDate || ''}
                        onChange={(e) => setNewEducation({ ...newEducation, endDate: e.target.value })}
                        disabled={newEducation.isCurrent}
                      />
                      <div className="flex items-center gap-2">
                        <Checkbox
                          id="isCurrent"
                          checked={newEducation.isCurrent || false}
                          onCheckedChange={(checked) =>
                            setNewEducation({
                              ...newEducation,
                              isCurrent: checked as boolean,
                              endDate: checked ? '' : newEducation.endDate
                            })
                          }
                        />
                        <Label htmlFor="isCurrent" className="text-sm">
                          {t('profile.education.currentlyStudy')}
                        </Label>
                      </div>
                    </div>
                  </div>

                  {showDocumentUpload && (
                    <div className="space-y-2">
                      <Label htmlFor="diplomaDocument">{t('profile.education.diplomaDocument')}</Label>
                      <div className="flex items-center gap-3">
                        <Input
                          id="diplomaDocument"
                          type="file"
                          accept=".pdf,.jpg,.jpeg,.png"
                          onChange={async (e) => {
                            const file = e.target.files?.[0];
                            if (file) {
                              const documentUrl = await handleDocumentUpload(file);
                              setNewEducation({ ...newEducation, diplomaDocument: documentUrl });
                            }
                          }}
                          className="flex-1"
                        />
                        {newEducation.diplomaDocument && (
                          <Button variant="outline" size="sm">
                            <Download className="h-4 w-4" />
                          </Button>
                        )}
                      </div>
                      <p className="text-xs text-gray-500">
                        {t('profile.education.documentHelp')}
                      </p>
                    </div>
                  )}

                  <div className="flex justify-end gap-2">
                    <Button variant="outline" onClick={() => setShowAddDialog(false)}>
                      {t('common.cancel')}
                    </Button>
                    <Button onClick={handleAddEducation}>
                      {t('common.add')}
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>

        <CardContent>
          {sortedEducation.length === 0 ? (
            <div className="text-center py-12 text-gray-500">
              <GraduationCap className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <h3 className="text-lg font-medium mb-2">{t('profile.education.noEducation')}</h3>
              <p className="text-sm mb-4">{t('profile.education.noEducation.description')}</p>
              <Button onClick={() => setShowAddDialog(true)}>
                {t('profile.education.addFirst')}
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {sortedEducation.map((edu, index) => (
                <Card key={edu.id} className="hover:shadow-md transition-shadow">
                  <CardContent className="p-6">
                    <div className="flex gap-4">
                      {enableReordering && (
                        <div className="flex flex-col gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleReorderEducation(index, 'up')}
                            disabled={index === 0}
                            className="h-8 w-8 p-0"
                          >
                            <MoveUp className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleReorderEducation(index, 'down')}
                            disabled={index === sortedEducation.length - 1}
                            className="h-8 w-8 p-0"
                          >
                            <MoveDown className="h-4 w-4" />
                          </Button>
                        </div>
                      )}

                      <div className="flex-1">
                        <div className="flex items-start justify-between mb-3">
                          <div>
                            <h3 className="text-lg font-semibold text-gray-900 mb-1">
                              {edu.degree}
                            </h3>
                            <div className="flex items-center gap-2 text-gray-600 mb-2">
                              <Building className="h-4 w-4" />
                              <span className="font-medium">{edu.institution}</span>
                            </div>
                            <div className="flex items-center gap-2 text-gray-600 mb-2">
                              <Award className="h-4 w-4" />
                              <span>{edu.field}</span>
                            </div>
                            <div className="flex items-center gap-2 text-sm text-gray-600">
                              <Calendar className="h-4 w-4" />
                              <span>
                                {formatDate(edu.startDate)} - {edu.isCurrent ? t('profile.education.present') : formatDate(edu.endDate)}
                              </span>
                              <span className="text-gray-400">
                                ({calculateDuration(edu.startDate, edu.endDate || '', edu.isCurrent)})
                              </span>
                              {edu.isCurrent && (
                                <Badge variant="default" className="bg-green-100 text-green-800">
                                  {t('profile.education.current')}
                                </Badge>
                              )}
                            </div>
                          </div>

                          <div className="flex items-center gap-2">
                            {edu.diplomaDocument && (
                              <Button variant="ghost" size="sm">
                                <FileText className="h-4 w-4" />
                              </Button>
                            )}
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => toggleExpanded(edu.id)}
                            >
                              {expandedItems.has(edu.id) ? (
                                <ChevronUp className="h-4 w-4" />
                              ) : (
                                <ChevronDown className="h-4 w-4" />
                              )}
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => setEditingEducation(edu)}
                            >
                              <Edit2 className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteEducation(edu.id)}
                              className="text-red-600 hover:text-red-700"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>

                        {/* Expanded content */}
                        {expandedItems.has(edu.id) && (
                          <div className="mt-4 space-y-3">
                            {edu.diplomaDocument && (
                              <div className="flex items-center gap-2 p-3 bg-gray-50 rounded-lg">
                                <FileText className="h-4 w-4 text-gray-600" />
                                <span className="text-sm text-gray-600">
                                  {t('profile.education.documentUploaded')}
                                </span>
                                <Button variant="outline" size="sm" className="ml-auto">
                                  <Download className="h-4 w-4 mr-1" />
                                  {t('common.download')}
                                </Button>
                              </div>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Education Dialog */}
      {editingEducation && (
        <Dialog open={!!editingEducation} onOpenChange={() => setEditingEducation(null)}>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{t('profile.education.edit')}</DialogTitle>
            </DialogHeader>

            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="editInstitution">{t('profile.education.institution')} *</Label>
                <Input
                  id="editInstitution"
                  value={editingEducation.institution}
                  onChange={(e) => setEditingEducation({ ...editingEducation, institution: e.target.value })}
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="editDegree">{t('profile.education.degree')} *</Label>
                  <Select
                    value={editingEducation.degree}
                    onValueChange={(value) => setEditingEducation({ ...editingEducation, degree: value })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {EDUCATION_LEVELS.map(level => (
                        <SelectItem key={level.value} value={level.label}>
                          {level.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="editField">{t('profile.education.field')} *</Label>
                  <Input
                    id="editField"
                    value={editingEducation.field}
                    onChange={(e) => setEditingEducation({ ...editingEducation, field: e.target.value })}
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="editStartDate">{t('profile.education.startDate')} *</Label>
                  <Input
                    id="editStartDate"
                    type="month"
                    value={editingEducation.startDate}
                    onChange={(e) => setEditingEducation({ ...editingEducation, startDate: e.target.value })}
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="editEndDate">{t('profile.education.endDate')}</Label>
                  <Input
                    id="editEndDate"
                    type="month"
                    value={editingEducation.endDate || ''}
                    onChange={(e) => setEditingEducation({ ...editingEducation, endDate: e.target.value })}
                    disabled={editingEducation.isCurrent}
                  />
                  <div className="flex items-center gap-2">
                    <Checkbox
                      id="editIsCurrent"
                      checked={editingEducation.isCurrent}
                      onCheckedChange={(checked) =>
                        setEditingEducation({
                          ...editingEducation,
                          isCurrent: checked as boolean,
                          endDate: checked ? '' : editingEducation.endDate
                        })
                      }
                    />
                    <Label htmlFor="editIsCurrent" className="text-sm">
                      {t('profile.education.currentlyStudy')}
                    </Label>
                  </div>
                </div>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setEditingEducation(null)}>
                  {t('common.cancel')}
                </Button>
                <Button onClick={handleUpdateEducation}>
                  {t('common.save')}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Tips */}
      <Alert>
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>
          <div className="space-y-2">
            <p className="font-medium">{t('profile.education.tips.title')}:</p>
            <ul className="space-y-1 text-sm">
              <li>â€¢ {t('profile.education.tips.beAccurate')}</li>
              <li>â€¢ {t('profile.education.tips.includeDates')}</li>
              <li>â€¢ {t('profile.education.tips.uploadDocuments')}</li>
              <li>â€¢ {t('profile.education.tips.listInReverse')}</li>
              <li>â€¢ {t('profile.education.tips.includeRelevant')}</li>
            </ul>
          </div>
        </AlertDescription>
      </Alert>
    </div>
  );
};

import React, { useState, useCallback } from 'react';
import { useTranslation } from 'react-i18next';
import {
  Briefcase,
  Plus,
  Calendar,
  MapPin,
  X,
  Edit2,
  Trash2,
  ChevronDown,
  ChevronUp,
  Building,
  ExternalLink,
  Check,
  AlertCircle,
  MoveUp,
  MoveDown,
  GripVertical
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { toast } from 'sonner';

import type { ExperienceItem } from '@/types/profile';

interface ExperienceManagerProps {
  experience: ExperienceItem[];
  onChange: (experience: ExperienceItem[]) => void;
  maxItems?: number;
  enableReordering?: boolean;
  showTechnologies?: boolean;
}

const COMMON_TECHNOLOGIES = [
  'JavaScript', 'TypeScript', 'React', 'Vue.js', 'Angular', 'Node.js',
  'Python', 'Java', 'C++', 'Go', 'Rust', 'PHP', 'Ruby',
  'HTML', 'CSS', 'Sass', 'Tailwind CSS', 'Bootstrap',
  'MongoDB', 'PostgreSQL', 'MySQL', 'Redis', 'Elasticsearch',
  'AWS', 'Google Cloud', 'Azure', 'Docker', 'Kubernetes',
  'Git', 'CI/CD', 'Jenkins', 'GitHub Actions', 'Agile', 'Scrum'
];

const INDUSTRY_TYPES = [
  'Technology', 'Finance', 'Healthcare', 'Education', 'E-commerce',
  'Media', 'Consulting', 'Manufacturing', 'Real Estate', 'Government',
  'Non-profit', 'Retail', 'Transportation', 'Energy', 'Telecommunications'
];

export const ExperienceManager: React.FC<ExperienceManagerProps> = ({
  experience,
  onChange,
  maxItems = 10,
  enableReordering = true,
  showTechnologies = true
}) => {
  const { t } = useTranslation();
  const [isAddingExperience, setIsAddingExperience] = useState(false);
  const [editingExperience, setEditingExperience] = useState<ExperienceItem | null>(null);
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [newExperience, setNewExperience] = useState<Partial<ExperienceItem>>({
    company: '',
    position: '',
    description: '',
    startDate: '',
    endDate: '',
    isCurrent: false,
    location: '',
    technologies: []
  });

  const handleAddExperience = useCallback(() => {
    if (!newExperience.company?.trim() || !newExperience.position?.trim()) {
      toast.error(t('profile.experience.requiredFields'));
      return;
    }

    if (experience.length >= maxItems) {
      toast.error(t('profile.experience.maxReached', { max: maxItems }));
      return;
    }

    const experienceItem: ExperienceItem = {
      id: Date.now().toString(),
      company: newExperience.company!,
      position: newExperience.position!,
      description: newExperience.description || '',
      startDate: newExperience.startDate!,
      endDate: newExperience.isCurrent ? '' : newExperience.endDate || '',
      isCurrent: newExperience.isCurrent || false,
      location: newExperience.location || '',
      technologies: newExperience.technologies || []
    };

    onChange([...experience, experienceItem]);
    setNewExperience({
      company: '',
      position: '',
      description: '',
      startDate: '',
      endDate: '',
      isCurrent: false,
      location: '',
      technologies: []
    });
    setShowAddDialog(false);
    toast.success(t('profile.experience.added'));
  }, [newExperience, experience, onChange, maxItems, t]);

  const handleUpdateExperience = useCallback(() => {
    if (!editingExperience || !editingExperience.company?.trim() || !editingExperience.position?.trim()) {
      toast.error(t('profile.experience.requiredFields'));
      return;
    }

    const updatedExperience = experience.map(exp =>
      exp.id === editingExperience.id ? editingExperience : exp
    );
    onChange(updatedExperience);
    setEditingExperience(null);
    toast.success(t('profile.experience.updated'));
  }, [editingExperience, experience, onChange, t]);

  const handleDeleteExperience = useCallback((experienceId: string) => {
    const updatedExperience = experience.filter(exp => exp.id !== experienceId);
    onChange(updatedExperience);
    toast.success(t('profile.experience.deleted'));
  }, [experience, onChange, t]);

  const handleReorderExperience = useCallback((index: number, direction: 'up' | 'down') => {
    if (!enableReordering) return;

    const reorderedExperience = [...experience];
    const targetIndex = direction === 'up' ? index - 1 : index + 1;

    if (targetIndex >= 0 && targetIndex < reorderedExperience.length) {
      [reorderedExperience[index], reorderedExperience[targetIndex]] =
      [reorderedExperience[targetIndex], reorderedExperience[index]];
      onChange(reorderedExperience);
    }
  }, [experience, onChange, enableReordering]);

  const toggleExpanded = useCallback((id: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(id)) {
      newExpanded.delete(id);
    } else {
      newExpanded.add(id);
    }
    setExpandedItems(newExpanded);
  }, [expandedItems]);

  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('zh-CN', { year: 'numeric', month: 'long' });
  };

  const calculateDuration = (startDate: string, endDate: string, isCurrent: boolean) => {
    const start = new Date(startDate);
    const end = isCurrent ? new Date() : new Date(endDate);

    const months = (end.getFullYear() - start.getFullYear()) * 12 + (end.getMonth() - start.getMonth());
    const years = Math.floor(months / 12);
    const remainingMonths = months % 12;

    if (years > 0) {
      return remainingMonths > 0
        ? `${years}å¹´${remainingMonths}ä¸ªæœˆ`
        : `${years}å¹´`;
    }
    return `${remainingMonths}ä¸ªæœˆ`;
  };

  const sortExperienceByDate = (items: ExperienceItem[]) => {
    return [...items].sort((a, b) => {
      const dateA = a.isCurrent ? new Date() : new Date(a.endDate || a.startDate);
      const dateB = b.isCurrent ? new Date() : new Date(b.endDate || b.startDate);
      return dateB.getTime() - dateA.getTime();
    });
  };

  const sortedExperience = sortExperienceByDate(experience);

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Briefcase className="h-5 w-5" />
              {t('profile.experience.title')}
              <Badge variant="outline">{experience.length}</Badge>
            </CardTitle>

            <Dialog open={showAddDialog} onOpenChange={setShowAddDialog}>
              <DialogTrigger asChild>
                <Button
                  disabled={experience.length >= maxItems}
                  className="flex items-center gap-2"
                >
                  <Plus className="h-4 w-4" />
                  {t('profile.experience.add')}
                </Button>
              </DialogTrigger>

              <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>{t('profile.experience.addNew')}</DialogTitle>
                </DialogHeader>

                <div className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="company">{t('profile.experience.company')} *</Label>
                      <Input
                        id="company"
                        value={newExperience.company || ''}
                        onChange={(e) => setNewExperience({ ...newExperience, company: e.target.value })}
                        placeholder={t('profile.experience.company.placeholder')}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="position">{t('profile.experience.position')} *</Label>
                      <Input
                        id="position"
                        value={newExperience.position || ''}
                        onChange={(e) => setNewExperience({ ...newExperience, position: e.target.value })}
                        placeholder={t('profile.experience.position.placeholder')}
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="startDate">{t('profile.experience.startDate')} *</Label>
                      <Input
                        id="startDate"
                        type="month"
                        value={newExperience.startDate || ''}
                        onChange={(e) => setNewExperience({ ...newExperience, startDate: e.target.value })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="endDate">{t('profile.experience.endDate')}</Label>
                      <Input
                        id="endDate"
                        type="month"
                        value={newExperience.endDate || ''}
                        onChange={(e) => setNewExperience({ ...newExperience, endDate: e.target.value })}
                        disabled={newExperience.isCurrent}
                      />
                      <div className="flex items-center gap-2">
                        <Checkbox
                          id="isCurrent"
                          checked={newExperience.isCurrent || false}
                          onCheckedChange={(checked) =>
                            setNewExperience({
                              ...newExperience,
                              isCurrent: checked as boolean,
                              endDate: checked ? '' : newExperience.endDate
                            })
                          }
                        />
                        <Label htmlFor="isCurrent" className="text-sm">
                          {t('profile.experience.currentlyWork')}
                        </Label>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="location">{t('profile.experience.location')}</Label>
                    <Input
                      id="location"
                      value={newExperience.location || ''}
                      onChange={(e) => setNewExperience({ ...newExperience, location: e.target.value })}
                      placeholder={t('profile.experience.location.placeholder')}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="description">{t('profile.experience.description')}</Label>
                    <Textarea
                      id="description"
                      value={newExperience.description || ''}
                      onChange={(e) => setNewExperience({ ...newExperience, description: e.target.value })}
                      placeholder={t('profile.experience.description.placeholder')}
                      rows={4}
                    />
                  </div>

                  {showTechnologies && (
                    <div className="space-y-2">
                      <Label>{t('profile.experience.technologies')}</Label>
                      <div className="flex flex-wrap gap-2 mb-2">
                        {COMMON_TECHNOLOGIES.map(tech => (
                          <Badge
                            key={tech}
                            variant={newExperience.technologies?.includes(tech) ? "default" : "outline"}
                            className="cursor-pointer"
                            onClick={() => {
                              const technologies = newExperience.technologies || [];
                              if (technologies.includes(tech)) {
                                setNewExperience({
                                  ...newExperience,
                                  technologies: technologies.filter(t => t !== tech)
                                });
                              } else {
                                setNewExperience({
                                  ...newExperience,
                                  technologies: [...technologies, tech]
                                });
                              }
                            }}
                          >
                            {tech}
                          </Badge>
                        ))}
                      </div>
                      <Input
                        placeholder={t('profile.experience.technologies.custom')}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter') {
                            const target = e.target as HTMLInputElement;
                            const tech = target.value.trim();
                            if (tech && !newExperience.technologies?.includes(tech)) {
                              setNewExperience({
                                ...newExperience,
                                technologies: [...(newExperience.technologies || []), tech]
                              });
                              target.value = '';
                            }
                          }
                        }}
                      />
                    </div>
                  )}

                  <div className="flex justify-end gap-2">
                    <Button variant="outline" onClick={() => setShowAddDialog(false)}>
                      {t('common.cancel')}
                    </Button>
                    <Button onClick={handleAddExperience}>
                      {t('common.add')}
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>

        <CardContent>
          {sortedExperience.length === 0 ? (
            <div className="text-center py-12 text-gray-500">
              <Briefcase className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <h3 className="text-lg font-medium mb-2">{t('profile.experience.noExperience')}</h3>
              <p className="text-sm mb-4">{t('profile.experience.noExperience.description')}</p>
              <Button onClick={() => setShowAddDialog(true)}>
                {t('profile.experience.addFirst')}
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {sortedExperience.map((exp, index) => (
                <Card key={exp.id} className="hover:shadow-md transition-shadow">
                  <CardContent className="p-6">
                    <div className="flex gap-4">
                      {enableReordering && (
                        <div className="flex flex-col gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleReorderExperience(index, 'up')}
                            disabled={index === 0}
                            className="h-8 w-8 p-0"
                          >
                            <MoveUp className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleReorderExperience(index, 'down')}
                            disabled={index === sortedExperience.length - 1}
                            className="h-8 w-8 p-0"
                          >
                            <MoveDown className="h-4 w-4" />
                          </Button>
                        </div>
                      )}

                      <div className="flex-1">
                        <div className="flex items-start justify-between mb-3">
                          <div>
                            <h3 className="text-lg font-semibold text-gray-900 mb-1">
                              {exp.position}
                            </h3>
                            <div className="flex items-center gap-2 text-gray-600 mb-2">
                              <Building className="h-4 w-4" />
                              <span className="font-medium">{exp.company}</span>
                              {exp.location && (
                                <>
                                  <MapPin className="h-4 w-4 ml-2" />
                                  <span>{exp.location}</span>
                                </>
                              )}
                            </div>
                            <div className="flex items-center gap-2 text-sm text-gray-600">
                              <Calendar className="h-4 w-4" />
                              <span>
                                {formatDate(exp.startDate)} - {exp.isCurrent ? t('profile.experience.present') : formatDate(exp.endDate)}
                              </span>
                              <span className="text-gray-400">
                                ({calculateDuration(exp.startDate, exp.endDate || '', exp.isCurrent)})
                              </span>
                              {exp.isCurrent && (
                                <Badge variant="default" className="bg-green-100 text-green-800">
                                  {t('profile.experience.current')}
                                </Badge>
                              )}
                            </div>
                          </div>

                          <div className="flex items-center gap-2">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => toggleExpanded(exp.id)}
                            >
                              {expandedItems.has(exp.id) ? (
                                <ChevronUp className="h-4 w-4" />
                              ) : (
                                <ChevronDown className="h-4 w-4" />
                              )}
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => setEditingExperience(exp)}
                            >
                              <Edit2 className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteExperience(exp.id)}
                              className="text-red-600 hover:text-red-700"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>

                        {/* Expanded content */}
                        {expandedItems.has(exp.id) && (
                          <div className="mt-4 space-y-3">
                            {exp.description && (
                              <div>
                                <h4 className="font-medium text-gray-900 mb-2">
                                  {t('profile.experience.description')}
                                </h4>
                                <p className="text-gray-600 whitespace-pre-wrap">
                                  {exp.description}
                                </p>
                              </div>
                            )}

                            {showTechnologies && exp.technologies && exp.technologies.length > 0 && (
                              <div>
                                <h4 className="font-medium text-gray-900 mb-2">
                                  {t('profile.experience.technologies')}
                                </h4>
                                <div className="flex flex-wrap gap-2">
                                  {exp.technologies.map((tech, techIndex) => (
                                    <Badge key={techIndex} variant="secondary">
                                      {tech}
                                    </Badge>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Experience Dialog */}
      {editingExperience && (
        <Dialog open={!!editingExperience} onOpenChange={() => setEditingExperience(null)}>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{t('profile.experience.edit')}</DialogTitle>
            </DialogHeader>

            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="editCompany">{t('profile.experience.company')} *</Label>
                  <Input
                    id="editCompany"
                    value={editingExperience.company}
                    onChange={(e) => setEditingExperience({ ...editingExperience, company: e.target.value })}
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="editPosition">{t('profile.experience.position')} *</Label>
                  <Input
                    id="editPosition"
                    value={editingExperience.position}
                    onChange={(e) => setEditingExperience({ ...editingExperience, position: e.target.value })}
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="editStartDate">{t('profile.experience.startDate')} *</Label>
                  <Input
                    id="editStartDate"
                    type="month"
                    value={editingExperience.startDate}
                    onChange={(e) => setEditingExperience({ ...editingExperience, startDate: e.target.value })}
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="editEndDate">{t('profile.experience.endDate')}</Label>
                  <Input
                    id="editEndDate"
                    type="month"
                    value={editingExperience.endDate || ''}
                    onChange={(e) => setEditingExperience({ ...editingExperience, endDate: e.target.value })}
                    disabled={editingExperience.isCurrent}
                  />
                  <div className="flex items-center gap-2">
                    <Checkbox
                      id="editIsCurrent"
                      checked={editingExperience.isCurrent}
                      onCheckedChange={(checked) =>
                        setEditingExperience({
                          ...editingExperience,
                          isCurrent: checked as boolean,
                          endDate: checked ? '' : editingExperience.endDate
                        })
                      }
                    />
                    <Label htmlFor="editIsCurrent" className="text-sm">
                      {t('profile.experience.currentlyWork')}
                    </Label>
                  </div>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="editLocation">{t('profile.experience.location')}</Label>
                <Input
                  id="editLocation"
                  value={editingExperience.location || ''}
                  onChange={(e) => setEditingExperience({ ...editingExperience, location: e.target.value })}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="editDescription">{t('profile.experience.description')}</Label>
                <Textarea
                  id="editDescription"
                  value={editingExperience.description || ''}
                  onChange={(e) => setEditingExperience({ ...editingExperience, description: e.target.value })}
                  rows={4}
                />
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setEditingExperience(null)}>
                  {t('common.cancel')}
                </Button>
                <Button onClick={handleUpdateExperience}>
                  {t('common.save')}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Tips */}
      <Alert>
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>
          <div className="space-y-2">
            <p className="font-medium">{t('profile.experience.tips.title')}:</p>
            <ul className="space-y-1 text-sm">
              <li>â€¢ {t('profile.experience.tips.beSpecific')}</li>
              <li>â€¢ {t('profile.experience.tips.quantifyAchievements')}</li>
              <li>â€¢ {t('profile.experience.tips.useKeywords')}</li>
              <li>â€¢ {t('profile.experience.tips.focusOnRelevant')}</li>
              <li>â€¢ {t('profile.experience.tips.includeDates')}</li>
            </ul>
          </div>
        </AlertDescription>
      </Alert>
    </div>
  );
};

import React, { useState, useCallback, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import {
  Image as ImageIcon,
  Video,
  FileText,
  Plus,
  X,
  Edit2,
  Trash2,
  Eye,
  ExternalLink,
  Upload,
  Calendar,
  Tag,
  Play,
  Maximize2,
  Check,
  AlertCircle,
  Star,
  Grid,
  List
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { toast } from 'sonner';

import type { PortfolioItem, PortfolioMedia } from '@/types/profile';

interface PortfolioManagerProps {
  portfolio: PortfolioItem[];
  onChange: (portfolio: PortfolioItem[]) => void;
  maxItems?: number;
  maxImagesPerItem?: number;
  enableVideo?: boolean;
  enableDocuments?: boolean;
}

const SUPPORTED_IMAGE_TYPES = ['image/jpeg', 'image/png', 'image/webp', 'image/gif'];
const SUPPORTED_VIDEO_TYPES = ['video/mp4', 'video/webm', 'video/ogg'];
const SUPPORTED_DOCUMENT_TYPES = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];

const COMMON_TAGS = [
  'Web Design', 'Mobile App', 'UI/UX', 'Branding', 'Logo Design',
  'Frontend', 'Backend', 'Full Stack', 'React', 'Vue.js', 'Angular',
  'Node.js', 'Python', 'E-commerce', 'SaaS', 'Mobile First',
  'Responsive', 'Animation', 'Illustration', 'Photography', 'Video'
];

const MAX_FILE_SIZE = 10 * 1024 * 1024; // 10MB per file
const MAX_IMAGES_PER_ITEM = 10;

export const PortfolioManager: React.FC<PortfolioManagerProps> = ({
  portfolio,
  onChange,
  maxItems = 20,
  maxImagesPerItem = MAX_IMAGES_PER_ITEM,
  enableVideo = true,
  enableDocuments = true
}) => {
  const { t } = useTranslation();
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [isAddingItem, setIsAddingItem] = useState(false);
  const [editingItem, setEditingItem] = useState<PortfolioItem | null>(null);
  const [showPreviewDialog, setShowPreviewDialog] = useState(false);
  const [previewItem, setPreviewItem] = useState<PortfolioItem | null>(null);
  const [uploadProgress, setUploadProgress] = useState<Record<string, number>>({});
  const [uploadErrors, setUploadErrors] = useState<string[]>([]);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const [newItem, setNewItem] = useState<Partial<PortfolioItem>>({
    title: '',
    description: '',
    images: [],
    projectUrl: '',
    technologies: [],
    completedAt: new Date().toISOString().split('T')[0]
  });

  const handleAddItem = useCallback(() => {
    if (!newItem.title?.trim()) {
      toast.error(t('profile.portfolio.titleRequired'));
      return;
    }

    if (portfolio.length >= maxItems) {
      toast.error(t('profile.portfolio.maxReached', { max: maxItems }));
      return;
    }

    const portfolioItem: PortfolioItem = {
      id: Date.now().toString(),
      title: newItem.title!,
      description: newItem.description || '',
      images: newItem.images || [],
      projectUrl: newItem.projectUrl || '',
      technologies: newItem.technologies || [],
      completedAt: newItem.completedAt || new Date().toISOString()
    };

    onChange([...portfolio, portfolioItem]);
    setNewItem({
      title: '',
      description: '',
      images: [],
      projectUrl: '',
      technologies: [],
      completedAt: new Date().toISOString().split('T')[0]
    });
    setIsAddingItem(false);
    toast.success(t('profile.portfolio.itemAdded'));
  }, [newItem, portfolio, onChange, maxItems, t]);

  const handleUpdateItem = useCallback(() => {
    if (!editingItem || !editingItem.title?.trim()) {
      toast.error(t('profile.portfolio.titleRequired'));
      return;
    }

    const updatedPortfolio = portfolio.map(item =>
      item.id === editingItem.id ? editingItem : item
    );
    onChange(updatedPortfolio);
    setEditingItem(null);
    toast.success(t('profile.portfolio.itemUpdated'));
  }, [editingItem, portfolio, onChange, t]);

  const handleDeleteItem = useCallback((itemId: string) => {
    const updatedPortfolio = portfolio.filter(item => item.id !== itemId);
    onChange(updatedPortfolio);
    toast.success(t('profile.portfolio.itemDeleted'));
  }, [portfolio, onChange, t]);

  const handleFileUpload = useCallback(async (files: FileList | null, targetItem: Partial<PortfolioItem>) => {
    if (!files || files.length === 0) return;

    const validFiles = Array.from(files).filter(file => {
      const isValidType = [
        ...SUPPORTED_IMAGE_TYPES,
        ...(enableVideo ? SUPPORTED_VIDEO_TYPES : []),
        ...(enableDocuments ? SUPPORTED_DOCUMENT_TYPES : [])
      ].includes(file.type);

      const isValidSize = file.size <= MAX_FILE_SIZE;

      if (!isValidType) {
        setUploadErrors(prev => [...prev, t('profile.portfolio.invalidFileType', { fileName: file.name })]);
        return false;
      }

      if (!isValidSize) {
        setUploadErrors(prev => [...prev, t('profile.portfolio.fileTooLarge', { fileName: file.name })]);
        return false;
      }

      return true;
    });

    if (validFiles.length === 0) return;

    const currentImages = targetItem.images || [];
    if (currentImages.length + validFiles.length > maxImagesPerItem) {
      toast.error(t('profile.portfolio.tooManyFiles', { max: maxImagesPerItem }));
      return;
    }

    // Simulate file upload
    for (const file of validFiles) {
      const fileId = `${Date.now()}-${Math.random()}`;
      setUploadProgress(prev => ({ ...prev, [fileId]: 0 }));

      try {
        // Simulate upload progress
        for (let progress = 0; progress <= 100; progress += 10) {
          await new Promise(resolve => setTimeout(resolve, 100));
          setUploadProgress(prev => ({ ...prev, [fileId]: progress }));
        }

        // Create file URL (in production, this would be the URL from the server)
        const fileUrl = URL.createObjectURL(file);
        const updatedImages = [...currentImages, fileUrl];

        if (targetItem.id) {
          // Update existing item
          setEditingItem(prev => prev ? { ...prev, images: updatedImages } : null);
        } else {
          // Update new item
          setNewItem(prev => ({ ...prev, images: updatedImages }));
        }

        setUploadProgress(prev => {
          const newProgress = { ...prev };
          delete newProgress[fileId];
          return newProgress;
        });

      } catch (error) {
        console.error('Upload error:', error);
        setUploadErrors(prev => [...prev, t('profile.portfolio.uploadError', { fileName: file.name })]);
      }
    }
  }, [enableVideo, enableDocuments, maxImagesPerItem, t]);

  const handlePreviewItem = useCallback((item: PortfolioItem) => {
    setPreviewItem(item);
    setShowPreviewDialog(true);
  }, []);

  const handleAddTag = useCallback((tag: string, targetItem: Partial<PortfolioItem>) => {
    if (!tag.trim()) return;

    const currentTags = targetItem.technologies || [];
    if (currentTags.includes(tag)) return;

    const updatedTags = [...currentTags, tag];

    if (targetItem.id) {
      setEditingItem(prev => prev ? { ...prev, technologies: updatedTags } : null);
    } else {
      setNewItem(prev => ({ ...prev, technologies: updatedTags }));
    }
  }, []);

  const handleRemoveTag = useCallback((tag: string, targetItem: Partial<PortfolioItem>) => {
    const currentTags = targetItem.technologies || [];
    const updatedTags = currentTags.filter(t => t !== tag);

    if (targetItem.id) {
      setEditingItem(prev => prev ? { ...prev, technologies: updatedTags } : null);
    } else {
      setNewItem(prev => ({ ...prev, technologies: updatedTags }));
    }
  }, []);

  const getMediaType = (url: string): 'image' | 'video' | 'document' => {
    if (url.match(/\.(jpg|jpeg|png|gif|webp)$/i)) return 'image';
    if (url.match(/\.(mp4|webm|ogg)$/i)) return 'video';
    if (url.match(/\.(pdf|doc|docx)$/i)) return 'document';
    return 'image';
  };

  const formatDate = (dateString: string) => {
    try {
      return new Date(dateString).toLocaleDateString('zh-CN', {
        year: 'numeric',
        month: 'long'
      });
    } catch {
      return dateString;
    }
  };

  const sortedPortfolio = [...portfolio].sort((a, b) =>
    new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <ImageIcon className="h-5 w-5" />
              {t('profile.portfolio.title')}
              <Badge variant="outline">{portfolio.length}</Badge>
            </CardTitle>

            <div className="flex items-center gap-2">
              {/* View Mode Toggle */}
              <div className="flex border rounded-md">
                <Button
                  variant={viewMode === 'grid' ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setViewMode('grid')}
                  className="rounded-r-none"
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === 'list' ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setViewMode('list')}
                  className="rounded-l-none"
                >
                  <List className="h-4 w-4" />
                </Button>
              </div>

              <Dialog open={isAddingItem} onOpenChange={setIsAddingItem}>
                <DialogTrigger asChild>
                  <Button
                    disabled={portfolio.length >= maxItems}
                    className="flex items-center gap-2"
                  >
                    <Plus className="h-4 w-4" />
                    {t('profile.portfolio.addItem')}
                  </Button>
                </DialogTrigger>

                <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>{t('profile.portfolio.addNewItem')}</DialogTitle>
                  </DialogHeader>

                  <div className="space-y-6">
                    {/* Basic Information */}
                    <div className="space-y-4">
                      <div className="space-y-2">
                        <Label htmlFor="title">{t('profile.portfolio.title')} *</Label>
                        <Input
                          id="title"
                          value={newItem.title || ''}
                          onChange={(e) => setNewItem({ ...newItem, title: e.target.value })}
                          placeholder={t('profile.portfolio.title.placeholder')}
                        />
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="description">{t('profile.portfolio.description')}</Label>
                        <Textarea
                          id="description"
                          value={newItem.description || ''}
                          onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
                          placeholder={t('profile.portfolio.description.placeholder')}
                          rows={4}
                        />
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor="projectUrl">{t('profile.portfolio.projectUrl')}</Label>
                          <Input
                            id="projectUrl"
                            type="url"
                            value={newItem.projectUrl || ''}
                            onChange={(e) => setNewItem({ ...newItem, projectUrl: e.target.value })}
                            placeholder="https://example.com"
                          />
                        </div>

                        <div className="space-y-2">
                          <Label htmlFor="completedAt">{t('profile.portfolio.completedAt')}</Label>
                          <Input
                            id="completedAt"
                            type="date"
                            value={newItem.completedAt || ''}
                            onChange={(e) => setNewItem({ ...newItem, completedAt: e.target.value })}
                          />
                        </div>
                      </div>
                    </div>

                    {/* Media Upload */}
                    <div className="space-y-4">
                      <Label>{t('profile.portfolio.media')}</Label>
                      <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                        <div className="text-center">
                          <Upload className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                          <div className="space-y-2">
                            <p className="text-lg font-medium">{t('profile.portfolio.uploadMedia')}</p>
                            <p className="text-sm text-gray-600">
                              {t('profile.portfolio.uploadDescription')}
                            </p>
                            <p className="text-xs text-gray-500">
                              {t('profile.portfolio.supportedFormats')} â€¢ {t('profile.portfolio.maxSize', { size: '10MB' })}
                            </p>
                          </div>
                          <input
                            ref={fileInputRef}
                            type="file"
                            multiple
                            accept={[...SUPPORTED_IMAGE_TYPES, ...(enableVideo ? SUPPORTED_VIDEO_TYPES : [])].join(',')}
                            onChange={(e) => handleFileUpload(e.target.files, newItem)}
                            className="hidden"
                          />
                          <Button
                            onClick={() => fileInputRef.current?.click()}
                            className="mt-4"
                          >
                            {t('profile.portfolio.selectFiles')}
                          </Button>
                        </div>
                      </div>

                      {/* Upload Progress */}
                      {Object.entries(uploadProgress).map(([fileId, progress]) => (
                        <div key={fileId} className="space-y-2">
                          <div className="flex justify-between text-sm">
                            <span>{t('profile.portfolio.uploading')}</span>
                            <span>{progress}%</span>
                          </div>
                          <Progress value={progress} />
                        </div>
                      ))}

                      {/* Preview Uploaded Images */}
                      {newItem.images && newItem.images.length > 0 && (
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                          {newItem.images.map((image, index) => (
                            <div key={index} className="relative group">
                              <div className="aspect-square rounded-lg overflow-hidden bg-gray-100">
                                {getMediaType(image) === 'image' ? (
                                  <img
                                    src={image}
                                    alt={`Upload ${index + 1}`}
                                    className="w-full h-full object-cover"
                                  />
                                ) : getMediaType(image) === 'video' ? (
                                  <div className="w-full h-full flex items-center justify-center">
                                    <Video className="h-8 w-8 text-gray-400" />
                                  </div>
                                ) : (
                                  <div className="w-full h-full flex items-center justify-center">
                                    <FileText className="h-8 w-8 text-gray-400" />
                                  </div>
                                )}
                              </div>
                              <Button
                                variant="destructive"
                                size="sm"
                                className="absolute top-2 right-2 h-6 w-6 rounded-full p-0 opacity-0 group-hover:opacity-100 transition-opacity"
                                onClick={() => {
                                  const updatedImages = newItem.images?.filter((_, i) => i !== index) || [];
                                  setNewItem({ ...newItem, images: updatedImages });
                                }}
                              >
                                <X className="h-3 w-3" />
                              </Button>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>

                    {/* Technologies */}
                    <div className="space-y-4">
                      <Label>{t('profile.portfolio.technologies')}</Label>
                      <div className="flex flex-wrap gap-2 mb-3">
                        {newItem.technologies?.map((tech, index) => (
                          <Badge key={index} variant="secondary" className="flex items-center gap-1">
                            {tech}
                            <Button
                              variant="ghost"
                              size="sm"
