  const getExperienceColor = (years: number) => {
    if (years >= 10) return 'text-purple-600';
    if (years >= 5) return 'text-blue-600';
    if (years >= 2) return 'text-green-600';
    return 'text-gray-600';
  };

  return (
    <div className="space-y-6">
