import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useI18n } from '@/i18n';
import { useServicesStore } from '@/store/servicesStore';
import { cn } from '@/utils';

interface SearchBarProps {
  placeholder?: string;
  onSearch?: (query: string) => void;
  showSuggestions?: boolean;
  className?: string;
  size?: 'small' | 'medium' | 'large';
}

export const SearchBar: React.FC<SearchBarProps> = ({
  placeholder,
  onSearch,
  showSuggestions = true,
  className = '',
  size = 'medium',
}) => {
  const navigate = useNavigate();
  const { t } = useI18n();
  const { searchQuery, setSearchQuery, searchServices } = useServicesStore();

  const [query, setQuery] = useState(searchQuery);
  const [suggestions, setSuggestions] = useState<string[]>([]);
  const [showSuggestionsList, setShowSuggestionsList] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const inputRef = useRef<HTMLInputElement>(null);
  const suggestionsRef = useRef<HTMLDivElement>(null);

  // Popular search suggestions
  const popularSuggestions = [
    'logoè®¾è®¡',
    'ç½‘ç«™å¼€å‘',
    'UI/UXè®¾è®¡',
    'å†…å®¹å†™ä½œ',
    'è§†é¢‘å‰ªè¾‘',
    'SEOä¼˜åŒ–',
    'ç§»åŠ¨åº”ç”¨å¼€å‘',
    'å“ç‰Œè®¾è®¡',
    'ç¤¾äº¤åª’ä½“è¥é”€',
    'ç¿»è¯‘æœåŠ¡',
  ];

  // Debounced search
  useEffect(() => {
    const timer = setTimeout(() => {
      if (query && query.length >= 2) {
        handleSuggestions(query);
      } else {
        setSuggestions([]);
      }
    }, 300);

    return () => clearTimeout(timer);
  }, [query]);

  // Close suggestions when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        suggestionsRef.current &&
        !suggestionsRef.current.contains(event.target as Node) &&
        !inputRef.current?.contains(event.target as Node)
      ) {
        setShowSuggestionsList(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleSuggestions = (searchText: string) => {
    // Filter popular suggestions
    const filtered = popularSuggestions.filter(suggestion =>
      suggestion.toLowerCase().includes(searchText.toLowerCase())
    );

    setSuggestions(filtered.slice(0, 8));
    setShowSuggestionsList(filtered.length > 0);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setQuery(value);
    setSearchQuery(value);

    if (!value) {
      setShowSuggestionsList(false);
      setSuggestions([]);
    }
  };

  const handleSearch = async (searchTerm?: string) => {
    const searchValue = searchTerm || query;

    if (!searchValue.trim()) return;

    setIsLoading(true);
    setShowSuggestionsList(false);

    try {
      if (onSearch) {
        onSearch(searchValue);
      } else {
        await searchServices(searchValue);
        // Navigate to services page if not already there
        if (window.location.pathname !== '/services') {
          navigate('/services');
        }
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    handleSearch();
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSearch();
    } else if (e.key === 'Escape') {
      setShowSuggestionsList(false);
      inputRef.current?.blur();
    }
  };

  const handleSuggestionClick = (suggestion: string) => {
    setQuery(suggestion);
    setSearchQuery(suggestion);
    setShowSuggestionsList(false);
    handleSearch(suggestion);
  };

  const sizeClasses = {
    small: 'px-3 py-2 text-sm',
    medium: 'px-4 py-3 text-base',
    large: 'px-6 py-4 text-lg',
  };

  const iconSizeClasses = {
    small: 'w-4 h-4',
    medium: 'w-5 h-5',
    large: 'w-6 h-6',
  };

  return (
    <div className={cn('relative w-full max-w-2xl', className)}>
      <form onSubmit={handleSubmit} className="relative">
        <div className="relative">
          {/* Search Icon */}
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <svg
              className={cn(
                'text-gray-400',
                iconSizeClasses[size]
              )}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
              />
            </svg>
          </div>

          {/* Input */}
          <input
            ref={inputRef}
            type="text"
            value={query}
            onChange={handleInputChange}
            onKeyDown={handleKeyDown}
            onFocus={() => {
              if (query && suggestions.length > 0) {
                setShowSuggestionsList(true);
              }
            }}
            placeholder={placeholder || t('search.searchQuery')}
            className={cn(
              'block w-full pl-10 pr-12 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200',
              sizeClasses[size],
              isLoading && 'opacity-75'
            )}
          />

          {/* Clear Button */}
          {query && (
            <button
              type="button"
              onClick={() => {
                setQuery('');
                setSearchQuery('');
                setShowSuggestionsList(false);
                setSuggestions([]);
                inputRef.current?.focus();
              }}
              className="absolute inset-y-0 right-12 flex items-center pr-2"
            >
              <svg
                className={cn(
                  'text-gray-400 hover:text-gray-600 transition-colors duration-200',
                  iconSizeClasses[size]
                )}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
