    if (draft.data.title) return draft.data.title;
    if (draft.data.description) {
      const plainText = draft.data.description.replace(/<[^>]*>/g, '');
      return plainText.substring(0, 50) + (plainText.length > 50 ? '...' : '');
    }
    return 'æœªå‘½åè‰ç¨¿';
