              className="w-4 h-4 mr-2"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
              />
            </svg>
            è‰ç¨¿ç®¡ç†
            {drafts.length > 0 && (
              <span className="ml-2 px-2 py-0.5 bg-gray-100 text-gray-700 rounded-full text-xs">
                {drafts.length}
              </span>
            )}
          </Button>

          {/* Auto-save indicator */}
          {autoSave && (
            <div className="flex items-center space-x-2 text-sm text-gray-500">
              {isAutoSaving ? (
                <>
                  <div className="w-2 h-2 bg-yellow-500 rounded-full animate-pulse"></div>
                  <span>è‡ªåŠ¨ä¿å­˜ä¸­...</span>
                </>
              ) : lastAutoSave ? (
                <>
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span>ä¸Šæ¬¡ä¿å­˜: {formatRelativeTime(lastAutoSave)}</span>
                </>
              ) : (
                <>
                  <div className="w-2 h-2 bg-gray-400 rounded-full"></div>
                  <span>ç­‰å¾…è‡ªåŠ¨ä¿å­˜</span>
                </>
              )}
            </div>
          )}
        </div>

        <Button
          variant="ghost"
          size="sm"
          onClick={handleAutoSave}
          disabled={disabled || isAutoSaving}
        >
          {isAutoSaving ? 'ä¿å­˜ä¸­...' : 'ç«‹å³ä¿å­˜'}
