    }
  }, [packages, activePackageId]);

  const createPackage = (type: 'basic' | 'standard' | 'premium'): ServicePackage => {
    const basePackage = {
      id: `pkg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      name: '',
