// Chinese Form Component Interface
export interface ChineseFormProps extends Omit<AntdFormProps, 'layout' | 'size'> {
  /**
   * Form layout optimized for Chinese mobile users
   * @default 'vertical'
   */
  layout?: 'vertical' | 'horizontal' | 'inline';

