  /**
   * Show reset button
   * @default true
   */
  showReset?: boolean;

  /**
   * Show loading state
   * @default false
   */
  loading?: boolean;

  /**
   * Custom CSS classes using Tailwind
   */
  className?: string;

  /**
   * Form actions (custom buttons)
   */
