// Chinese Form Component
export const ChineseForm: React.FC<ChineseFormProps> = ({
  layout = 'vertical',
  size = 'md',
  submitText = 'æäº¤',
  resetText = 'é‡ç½®',
  showReset = true,
  loading = false,
  className,
  actions,
  children,
  onFinish,
  ...antdProps
}) => {
  // Size mapping
  const sizeMapping = {
    sm: 'small' as const,
    md: 'middle' as const,
    lg: 'large' as const,
  };

  // Form classes
  const formClasses = cn(
    'text-chinese',
    layout === 'vertical' && 'space-y-4',
    layout === 'horizontal' && 'grid grid-cols-1 md:grid-cols-3 gap-4',
    layout === 'inline' && 'flex flex-wrap items-center gap-4',
    className
  );

  // Handle form submission
  const handleFinish = async (values: any) => {
    if (onFinish) {
      await onFinish(values);
    }
  };

  // Default actions
  const defaultActions = (
    <div className={cn('flex gap-3', layout === 'inline' ? 'ml-4' : 'mt-6')}>
      <Button
        type="primary"
        htmlType="submit"
        loading={loading}
        size={sizeMapping[size]}
        className="min-h-[44px] px-6 font-medium"
      >
        {submitText}
