    }

    return {
      isValid: Object.keys(errors).length === 0,
      errors,
    };
  };

  return {
    validateEmail,
    validatePassword,
    validateLoginForm,
    validateRegisterForm,
    loginForm,
    registerForm,
    updateLoginForm,
    updateRegisterForm,
  };
}

import { useState, useEffect } from 'react';

export function useLocalStorage<T>(
  key: string,
  initialValue: T
): [T, (value: T | ((val: T) => T)) => void, () => void] {
  // Get from local storage then parse stored json or return initialValue
  const readValue = (): T => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.warn(`Error reading localStorage key "${key}":`, error);
      return initialValue;
    }
  };

  // State to store our value
  const [storedValue, setStoredValue] = useState<T>(readValue);

  // Return a wrapped version of useState's setter function that ...
  // ... persists the new value to localStorage.
  const setValue = (value: T | ((val: T) => T)) => {
    try {
      // Allow value to be a function so we have same API as useState
      const valueToStore = value instanceof Function ? value(storedValue) : value;

      // Save state
      setStoredValue(valueToStore);

      // Save to local storage
      if (typeof window !== 'undefined') {
        window.localStorage.setItem(key, JSON.stringify(valueToStore));
      }
    } catch (error) {
      console.warn(`Error setting localStorage key "${key}":`, error);
    }
  };

  // Remove the item from storage
  const removeValue = () => {
    try {
      if (typeof window !== 'undefined') {
        window.localStorage.removeItem(key);
      }
      setStoredValue(initialValue);
    } catch (error) {
      console.warn(`Error removing localStorage key "${key}":`, error);
    }
  };

  useEffect(() => {
    const handleStorageChange = () => {
      setStoredValue(readValue());
    };

    // this only works for other documents, not the current one
    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  return [storedValue, setValue, removeValue];
}

import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

// å¯¼å…¥ä¸­æ–‡ç¿»è¯‘æ–‡ä»¶
import zhCN from '../locales/zh-CN.json';

// å®šä¹‰ç¿»è¯‘èµ„æºçš„ç±»å‹
export type TranslationResources = typeof zhCN;

// åˆå§‹åŒ– i18next
i18n
  .use(initReactI18next)
  .init({
    resources: {
      'zh-CN': {
        translation: zhCN,
      },
    },
    lng: 'zh-CN',
    fallbackLng: 'zh-CN',
    interpolation: {
      escapeValue: false,
    },
  });

// å¯¼å‡º i18n å®ä¾‹
export default i18n;

// åˆ›å»ºç±»å‹å®‰å…¨çš„ç¿»è¯‘ hook
export const useTypedTranslation = () => {
  const { t } = i18n;

  return {
    t: (key: string, options?: any) => t(key, options),
    i18n,
  };
};

// ç¿»è¯‘å‡½æ•°å·¥å…·
export const translate = (key: string, options?: any): string => {
  return i18n.t(key, options);
};

// æ ¼å¼åŒ–æ•°å­—
export const formatNumber = (num: number): string => {
  return new Intl.NumberFormat('zh-CN').format(num);
};

// æ ¼å¼åŒ–è´§å¸
export const formatCurrency = (amount: number, currency: string = 'CNY'): string => {
  return new Intl.NumberFormat('zh-CN', {
    style: 'currency',
    currency,
  }).format(amount);
};

// æ ¼å¼åŒ–æ—¥æœŸ
export const formatDate = (date: string | Date): string => {
  return new Intl.DateTimeFormat('zh-CN', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  }).format(typeof date === 'string' ? new Date(date) : date);
};

// åˆ›å»ºReact Context Provider
import { createContext, useContext, ReactNode } from 'react';

interface I18nContextType {
  t: (key: string, options?: any) => string;
  formatNumber: (num: number) => string;
  formatCurrency: (amount: number, currency?: string) => string;
  formatDate: (date: string | Date) => string;
}

const I18nContext = createContext<I18nContextType | undefined>(undefined);

export const I18nProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const { t } = useTypedTranslation();

  const value: I18nContextType = {
    t,
    formatNumber,
    formatCurrency,
    formatDate,
  };

  return React.createElement(I18nContext.Provider, { value }, children);
};

export const useI18n = (): I18nContextType => {
  const context = useContext(I18nContext);
  if (!context) {
    throw new Error('useI18n must be used within an I18nProvider');
  }
  return context;
};

