  const handleVerificationComplete = useCallback((
    type: 'identity' | 'phone' | 'skills' | 'portfolio' | 'certifications',
    data: any
  ) => {
    setRegistrationData(prev => ({
      ...prev,
      [type === 'identity' ? 'identityDocuments' :
       type === 'phone' ? 'verifiedPhone' :
