        if (!registrationData.agreeToTerms) {
          newErrors.agreeToTerms = 'è¯·åŒæ„æœåŠ¡æ¡æ¬¾';
        }
        break;

      case 2:
        if (registrationData.identityDocuments.length === 0) {
          newErrors.identity = 'è¯·å®Œæˆèº«ä»½éªŒè¯';
        }
        break;

      case 3:
        if (!registrationData.verifiedPhone) {
          newErrors.phone = 'è¯·å®Œæˆæ‰‹æœºéªŒè¯';
        }
        break;

      case 4:
        if (registrationData.skills.length === 0) {
          newErrors.skills = 'è¯·è‡³å°‘æ·»åŠ ä¸€ä¸ªæŠ€èƒ½';
        }
