          validationErrors.push('è¯·è‡³å°‘æ·»åŠ ä¸€ä¸ªæŠ€èƒ½');
        }
        break;

      case 'portfolio':
        if (!profileData.portfolio || profileData.portfolio.length === 0) {
          validationErrors.push('è¯·è‡³å°‘æ·»åŠ ä¸€ä¸ªä½œå“é›†é¡¹ç›®');
        }
        break;

