                    <div className="text-sm text-gray-600">
                      {t('profile.settings.currentSession')}: Chrome on Windows â€¢ åŒ—äº¬, ä¸­å›½
                    </div>
                  </div>
                  <Button variant="outline">
                    <Eye className="h-4 w-4 mr-2" />
                    {t('profile.settings.viewAll')}
                  </Button>
