      return 'æƒé™ä¸è¶³';
    case 404:
      return 'è¯·æ±‚çš„èµ„æºä¸å­˜åœ¨';
    case 405:
      return 'è¯·æ±‚æ–¹æ³•ä¸è¢«å…è®¸';
    case 408:
      return 'è¯·æ±‚è¶…æ—¶';
    case 409:
      return 'è¯·æ±‚å†²çª';
    case 429:
      return 'è¯·æ±‚è¿‡äºé¢‘ç¹ï¼Œè¯·ç¨åå†è¯•';
    case 500:
      return 'æœåŠ¡å™¨å†…éƒ¨é”™è¯¯';
    case 502:
